$("head").append(
  '<link href="/assets/css/dark.min.css" id="dark" type="text/css" rel="stylesheet">'
),
  (window.onload = function () {
    var t = document.getElementById("scroll");
    t && (t.scrollTop = t.scrollHeight);
  }),
  $(document).ready(function () {
    (clicked = !0),
      $(".mode").click(function () {
        clicked
          ? ($("#dark").remove(), (clicked = !1))
          : ($("head").append(
              '<link href="/assets/css/dark.min.css" id="dark" type="text/css" rel="stylesheet">'
            ),
            (clicked = !0));
      });
  }),
  $(function () {
    "use strict";
    $('[data-chat="open"]').on("click", function () {
      $(".chat").toggleClass("open");
    });
  }),
  $(function () {
    "use strict";
    $('[data-utility="open"]').on("click", function () {
      $(".utility").toggleClass("open");
    });
  }),
  $(document).ready(function () {
    $(".filter").not(".direct").hide("3000"),
      $(".filter").not(".direct").hide("3000"),
      $(".filter-btn").click(function () {
        var t = $(this).attr("data-filter");
        $(".filter")
          .not("." + t)
          .hide("3000"),
          $(".filter")
            .filter("." + t)
            .show("3000");
      });
  });
